import React from 'react';
import axios from 'axios';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import { Link } from 'react-router-dom';
import { Container, Row, Col } from 'react-bootstrap';

class BookItem extends React.Component{

  constructor(){
    super();
    this.DeleteBook = this.DeleteBook.bind(this);
  }

  DeleteBook(e){
    console.log("Delete Clicked");
    axios.delete("http://localhost:4000/api/books/"+this.props.book._id)
    .then(()=>{
      this.props.ReloadDataMethod();
    })
    .catch();
  }

  render(){
      return(
          <div>
              {/* layout for books entered by user */}
              {/* <Card bg="light" style={{ width: '50rem' }}>
                <Card.Body>
                    <blockquote className="blockquote mb-0">
                      <Card.Title>{this.props.book.title}</Card.Title>
                      <Card.Subtitle>{this.props.book.author}</Card.Subtitle>
                      <Card.Text>{this.props.book.summary}</Card.Text>
                      <Card.Text>{this.props.book.selectedOption}</Card.Text>
                    </blockquote>
                </Card.Body>
                <Button variant="danger" onClick={this.DeleteBook}>Delete</Button>
                <Link to={"/edit/" + this.props.book._id} className="btn btn-primary">Edit</Link>
              </Card> */}
              <Container>
                <Row>
                  <Col xs ={{ order: 1}}>{this.props.book.title}</Col>
                  <Col xs ={{ order: 2}}>{this.props.book.author}</Col>
                  <Col xs ={{ order: 3}}>{this.props.book.summary}</Col>
                  <Col xs ={{ order: 4}}>{this.props.book.selectedOption}</Col>
                  <Col xs={{ order: 5}}><Button variant="danger" onClick={this.DeleteBook}>Delete</Button>
                  <Link to={"/edit/" + this.props.book._id} className="btn btn-primary">Edit</Link></Col>
                </Row>


              </Container>
          </div>
      )
  }
}
export default BookItem;
