import React, { Component } from "react";

class EnterBook extends React.Component{
    constructor(props) {
        super(props);
        this.state = { Title: '', Author: '', Summary: '' };

        this.handleChangeBookTitle = this.handleChangeBookTitle.bind(this);
        this.handleChangeBookAuthor = this.handleChangeBookAuthor.bind(this);
        this.handleChangeBookSummary = this.handleChangeBookSummary.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChangeBookTitle(e) {
        this.setState({ Title: e.target.value });
    }

    handleChangeBookAuthor(e) {
        this.setState({ Author: e.target.value });
    }

    handleChangeBookSummary(e) {
        this.setState({ Summary: e.target.value });
    }

    // called when submit button is hit
    handleSubmit(e) {
        alert('Title: ' + this.state.Title + "\nAuthor: " + this.state.Author + "\nSummary: " + this.state.Summary);
        e.preventDefault();

        const newBook = {
            title: this.state.Title,
            year: this.state.Author,
            poster: this.state.Summary
        }; 

        //axios.post('http://localhost:4000/api/movies', newBook)
        //.then()
        //.catch();

        this.setState({
            Title: '',
            Author: '',
            Summary: ''
        });
    }
    render(){
        return(
            <div>
                <h2>Enter Book</h2>

                <form onSubmit={this.handleSubmit}>
                    <div className = "form-group">
                        <label>
                            Book Title:
                        </label>
                        <input type="text" className="form-control" />
                    </div>
                    <div className = "form-group">
                        <label>
                            Author:
                        </label>
                        <input type="text" className="form-control" />
                    </div>
                    <div className = "form-group">
                        <label>
                            Book Summary:
                        </label>
                        <textarea className="form-control" rows="6"  />
                    </div>
                    <div className = "form-group">
                        <label>
                            Select Category Book Belongs to:
                        </label>
                    </div>
                    <div className="form-check">
                        <label>
                            <input
                                type="radio"
                                name="react-tips"
                                value="bookRead"
                                className="form-check-input"
                            />
                            Books Read
                        </label>
                    </div>

                    <div className="form-check">
                        <label>
                            <input
                                type="radio"
                                name="react-tips"
                                value="bookReading"
                                className="form-check-input"
                            />
                            Books Reading
                        </label>
                    </div>

                    <div className="form-check">
                        <label>
                            <input
                                type="radio"
                                name="react-tips"
                                value="bookWillRead"
                                className="form-check-input"
                            />
                            Books Will Read
                        </label>
                    </div>
                    <div className = "form-group">
                        <input type="submit" value="Submit" />
                    </div>
                </form>
            </div>

        )
    
    }
}
export default EnterBook;
