import React from 'react';
import Books from './books';
import axios from 'axios';
import EnterBook from './enterBook';

class Read extends React.Component{

    state = {
        books: []
    };

    componentDidMount() {
        axios.get('http://localhost:4001/api/books')
        .then((response)=>{
            this.setState({books: response.data.books})
        })
        .catch((error)=>{
            console.log(error);
        });
    }

    render(){
        if(this.state.selectedOption === "bookRead"){
            return(
                <div>
                    <h2>Books Finished Reading</h2>
                    <Books myBooks={this.state.books}></Books>
                </div>
            );
        }
        
    }
}
export default Read;
