import React, { Component } from "react";
import axios from 'axios';
import { newExpression } from '@babel/types';

class EnterBook extends React.Component{
    constructor(props) {
        super(props);
        this.state = { Title: '', Author: '', Summary: '', SelectedOption: '' };

        this.handleChangeBookTitle = this.handleChangeBookTitle.bind(this);
        this.handleChangeBookAuthor = this.handleChangeBookAuthor.bind(this);
        this.handleChangeBookSummary = this.handleChangeBookSummary.bind(this);
        this.handleOptionChange = this.handleOptionChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChangeBookTitle(e) {
        this.setState({ Title: e.target.value });
    }

    handleChangeBookAuthor(e) {
        this.setState({ Author: e.target.value });
    }

    handleChangeBookSummary(e) {
        this.setState({ Summary: e.target.value });
    }

    handleOptionChange(e) {
         this.setState({ SelectedOption: e.target.value });
    }

    // called when submit button is hit
    handleSubmit(e) {
        alert('Title: ' + this.state.Title + "\nAuthor: " + this.state.Author + "\nSummary: " + this.state.Summary + 
            "\nOption picked: " + this.state.SelectedOption);
        e.preventDefault();

        const newBook = {
            title: this.state.Title,
            author: this.state.Author,
            summary: this.state.Summary, 
            selectedOption : this.state.SelectedOption
        }; 

        axios.post('http://localhost:4001/api/books',newBook) 
          .then()
          .catch();

        this.setState({
            Title: '',
            Author: '',
            Summary: '',
            SelectedOption: ''
        });
    }

    render(){
        return(
            <div>
                <h2>Please Enter the Books Details</h2>

                <form onSubmit={this.handleSubmit}>
                    {/* Enter book details (name, author, summary) */}
                    <div className = "form-group">
                        <label>
                            Book Title:
                        </label>
                        <input type="text" className="form-control" value={this.state.Title} onChange={this.handleChangeBookTitle}/>
                    </div> {/* title */}

                    <div className = "form-group">
                        <label>
                            Author:
                        </label>
                        <input type="text" className="form-control" value={this.state.Author} onChange={this.handleChangeBookAuthor}/>
                    </div> {/* author */}

                    <div className = "form-group">
                        <label>
                            Book Summary:
                        </label>
                        <textarea className="form-control" rows="6" value={this.state.Summary} onChange={this.handleChangeBookSummary}/>
                    </div> {/* summary */}

                    {/* select category the book is in (finished, reading, will read) */}
                    <div className = "radio">
                        <p>
                            Select Category Book Belongs to:
                        </p>
                    </div>

                    <div className="radio">
                        <label>
                            <input type="radio" value="bookRead" className="form-control"
                                checked={this.state.SelectedOption === "bookRead"}
                                onChange={this.handleOptionChange}/>
                            Books Read
                        </label>
                    </div> {/* finished */}

                    <div className="radio">
                        <label>
                            <input type="radio" value="bookReading" className="form-control"
                                checked={this.state.SelectedOption === "bookReading"}
                                onChange={this.handleOptionChange}/>
                            Books Reading
                        </label>
                    </div> {/* reading */}

                    <div className="radio">
                        <label>
                            <input type="radio" value="bookWillRead" className="form-control"
                                checked={this.state.SelectedOption === "bookWillRead"}
                                onChange={this.handleOptionChange}/>
                            Books Will Read
                        </label>
                    </div> {/* will read */}

                    <div className = "form-group">
                        <input type="submit" value="Submit" />
                    </div> {/* submit */}
                </form>
            </div>
        )
    }
}

export default EnterBook;
